            <span>{item.title}</span>
          </Link>
          {item.submenu && (
            <div className="ml-6 mt-1 space-y-1">
              {item.submenu.map((subitem, subindex) => (
                <Link
                  key={subindex}
                  href={subitem.href}
                  className="block rounded-md px-3 py-1.5 text-sm hover:bg-accent hover:text-accent-foreground"
                >
                  {subitem.title}
                </Link>
              ))}
            </div>
          )}
        </div>
      ))}
    </nav>
  )
}

"use client"

import { Check, ChevronsUpDown } from "lucide-react"
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command"
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover"
import { useState } from "react"

const organizations = [
  {
    value: "minha-empresa",
    label: "Minha Empresa",
  },
  {
    value: "empresa-teste",
    label: "Empresa Teste",
  },
]

export function OrganizationSwitcher() {
  const [open, setOpen] = useState(false)
  const [value, setValue] = useState("minha-empresa")

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button variant="outline" role="combobox" aria-expanded={open} className="w-[180px] justify-between">
          {value ? organizations.find((organization) => organization.value === value)?.label : "Selecione..."}
          <ChevronsUpDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-[180px] p-0">
        <Command>
          <CommandInput placeholder="Buscar organização..." />
          <CommandList>
            <CommandEmpty>Nenhuma organização encontrada.</CommandEmpty>
            <CommandGroup>
              {organizations.map((organization) => (
                <CommandItem
                  key={organization.value}
                  value={organization.value}
                  onSelect={(currentValue) => {
                    setValue(currentValue === value ? "" : currentValue)
                    setOpen(false)
                  }}
                >
                  <Check className={cn("mr-2 h-4 w-4", value === organization.value ? "opacity-100" : "opacity-0")} />
                  {organization.label}
                </CommandItem>
              ))}
            </CommandGroup>
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  )
}

